package com.github.madamiak.aoc.day2;

import static com.github.madamiak.aoc.day2.Puzzle2_2.solve;
import static org.assertj.core.api.Assertions.assertThat;

import org.junit.Test;

public class Puzzle2_2Test {

	@Test
	public void shouldReturn5WhenULL() {
		assertThat(solve("ULL")).isEqualTo("5");
	}

	@Test
	public void shouldReturn5DB3WhenULL_RRDDD_LURDL_UUUUD() {
		assertThat(solve("ULL\n" +
				"RRDDD\n" +
				"LURDL\n" +
				"UUUUD")).isEqualTo("5DB3");
	}

	@Test
	public void answer() {
		System.out.println(Puzzle2_2.solve(
				"\n" +
						"\n" +
						"\n" +
						"\n" +
						""));

	}

}